import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Friends.css';

function Friends() {
  const [friendEmail, setFriendEmail] = useState('');
  const [friendRequests, setFriendRequests] = useState([]);
  const [friends, setFriends] = useState([]);

  useEffect(() => {
    fetchFriendRequests();
    fetchFriends();
  }, []);

  const fetchFriendRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friend-requests', {
        headers: { Authorization: localStorage.getItem('token') }
      });
      setFriendRequests(response.data);
    } catch (error) {
      console.error('Error fetching friend requests:', error);
    }
  };

  const fetchFriends = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friends', {
        headers: { Authorization: localStorage.getItem('token') }
      });
      setFriends(response.data);
    } catch (error) {
      console.error('Error fetching friends:', error);
    }
  };

  const sendFriendRequest = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/send-friend-request', 
        { friendEmail },
        { headers: { Authorization: localStorage.getItem('token') } }
      );
      alert('Friend request sent');
      setFriendEmail('');
    } catch (error) {
      alert('Error sending friend request');
    }
  };

  const acceptFriendRequest = async (friendId) => {
    try {
      await axios.post('http://localhost:5000/api/accept-friend-request', 
        { friendId },
        { headers: { Authorization: localStorage.getItem('token') } }
      );
      fetchFriendRequests();
      fetchFriends();
    } catch (error) {
      alert('Error accepting friend request');
    }
  };

  return (
    <div className="friends-container">
      <h2 className="friends-header">Friends Management</h2>
      
      <form onSubmit={sendFriendRequest} className="friend-form">
        <input
          type="email"
          placeholder="Friend's email"
          value={friendEmail}
          onChange={(e) => setFriendEmail(e.target.value)}
        />
        <button type="submit">Send Friend Request</button>
      </form>

      <div className="friends-section">
        <h3>Friend Requests</h3>
        {friendRequests.length === 0 ? (
          <p>No pending friend requests</p>
        ) : (
          <ul className="friends-list">
            {friendRequests.map(request => (
              <li key={request._id} className="friend-item">
                <span>{request.email}</span>
                <button onClick={() => acceptFriendRequest(request._id)}>Accept</button>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="friends-section">
        <h3>Friends List</h3>
        <p className="friend-count">You have {friends.length} friend(s)</p>
        {friends.length === 0 ? (
          <p>You don't have any friends yet</p>
        ) : (
          <ul className="friends-list">
            {friends.map(friend => (
              <li key={friend._id} className="friend-item">
                <span>{friend.email}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Friends;
